import type { LucideIcon } from "lucide-react";
import type { ReactNode } from "react";

export type DialogVariant = "default" | "destructive" | "warning";
export type DialogSize = "sm" | "md" | "lg";

export type DialogAction = {
  label: ReactNode;
  onClick?: () => void | Promise<void>;
  variant?: "default" | "destructive";
};

export type DialogCancel = {
  label: ReactNode;
  onClick?: () => void | Promise<void>;
};

export type DialogBaseConfig = {
  id?: string;
  title?: string;
  description?: ReactNode;
  icon?: LucideIcon;
  variant?: DialogVariant;
  size?: DialogSize;
  style?: "default" | "centered";
};

export type ConfirmDialogConfig = DialogBaseConfig & {
  type: "confirm";
  confirmText?: string;
  action: DialogAction;
  cancel?: DialogCancel;
};

export type InputDialogConfig = DialogBaseConfig & {
  type: "input";
  input: {
    label: string;
    defaultValue?: string;
    placeholder?: string;
  };
  action: Omit<DialogAction, "onClick"> & {
    onClick: (inputValue?: string) => void | Promise<void>;
  };
  cancel?: DialogCancel;
};

export type CustomDialogConfig = DialogBaseConfig & {
  type: "custom";
  children: ReactNode;
};

export type DialogConfig =
  | ConfirmDialogConfig
  | InputDialogConfig
  | CustomDialogConfig;

export type Dialog = DialogConfig & {
  id: string;
  loading?: boolean;
};

import type {
  ConfirmDialogConfig,
  CustomDialogConfig,
  Dialog,
  DialogConfig,
  InputDialogConfig,
} from "./dialog-types";

function generateDialogId(): string {
  return Math.random().toString(36).slice(2, 9);
}

export const DialogFactory = {
  confirm: (config: Omit<ConfirmDialogConfig, "type">): Dialog => ({
    ...config,
    type: "confirm",
    id: generateDialogId(),
    loading: false,
  }),

  input: (config: Omit<InputDialogConfig, "type">): Dialog => ({
    ...config,
    type: "input",
    id: generateDialogId(),
    loading: false,
  }),

  custom: (config: Omit<CustomDialogConfig, "type">): Dialog => ({
    ...config,
    type: "custom",
    id: generateDialogId(),
    loading: false,
  }),

  fromConfig: (config: Omit<DialogConfig, "id">): Dialog => {
    const id = generateDialogId();

    switch (config.type) {
      case "confirm":
        return { ...config, id, loading: false } as Dialog;
      case "input":
        return { ...config, id, loading: false } as Dialog;
      case "custom":
        return { ...config, id, loading: false } as Dialog;
      default:
        throw new Error(`Unknown dialog type`);
    }
  },
};

"use client";

import { toast } from "sonner";
import { create } from "zustand";
import { DialogFactory } from "./dialog-factory";
import type { Dialog, DialogConfig } from "./dialog-types";

type DialogStore = {
  dialogs: Dialog[];
  activeDialog: Dialog | null;

  addDialog: (config: Omit<DialogConfig, "id">) => string;
  removeDialog: (id: string) => void;
  setLoading: (id: string, loading: boolean) => void;
  clear: () => void;
};

export const useDialogStore = create<DialogStore>((set) => ({
  dialogs: [],
  activeDialog: null,

  addDialog: (config) => {
    const dialog = DialogFactory.fromConfig(config);

    set((state) => ({
      dialogs: [...state.dialogs, dialog],
      activeDialog: state.activeDialog ?? dialog,
    }));

    return dialog.id;
  },

  removeDialog: (id) =>
    set((state) => {
      const dialogs = state.dialogs.filter((d) => d.id !== id);
      return {
        dialogs,
        activeDialog: dialogs[0] ?? null,
      };
    }),

  setLoading: (id, loading) =>
    set((state) => ({
      dialogs: state.dialogs.map((d) => (d.id === id ? { ...d, loading } : d)),
      activeDialog:
        state.activeDialog?.id === id
          ? { ...state.activeDialog, loading }
          : state.activeDialog,
    })),

  clear: () => set({ dialogs: [], activeDialog: null }),
}));

export async function handleDialogAction(
  dialogId: string,
  action: () => void | Promise<void>,
): Promise<void> {
  const { setLoading, removeDialog } = useDialogStore.getState();

  try {
    setLoading(dialogId, true);

    const result = action();

    if (result instanceof Promise) {
      await result;
    }

    removeDialog(dialogId);
  } catch (error) {
    const err = error instanceof Error ? error : new Error("Unknown error");

    toast.error("Action failed", {
      description: err.message,
    });

    setLoading(dialogId, false);
  }
}

import { useDialogStore } from "./dialog-store";
import type {
  ConfirmDialogConfig,
  CustomDialogConfig,
  InputDialogConfig,
} from "./dialog-types";

export const dialogManager = {
  confirm: (config: Omit<ConfirmDialogConfig, "type" | "id">) =>
    useDialogStore.getState().addDialog({ ...config, type: "confirm" }),

  input: (config: Omit<InputDialogConfig, "type" | "id">) =>
    useDialogStore.getState().addDialog({ ...config, type: "input" }),

  custom: (config: Omit<CustomDialogConfig, "type" | "id">) =>
    useDialogStore.getState().addDialog({ ...config, type: "custom" }),

  close: (id: string) => useDialogStore.getState().removeDialog(id),

  closeAll: () => useDialogStore.getState().clear(),
};

"use client";

import {
  AlertDialog,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { cn } from "@/lib/utils";
import { useState } from "react";
import { handleDialogAction, useDialogStore } from "./dialog-store";
import type { Dialog } from "./dialog-types";

export function DialogComponent(props: { dialog: Dialog }) {
  const { dialog } = props;
  const [confirmText, setConfirmText] = useState("");
  const [inputValue, setInputValue] = useState(
    dialog.type === "input" ? dialog.input.defaultValue ?? "" : ""
  );

  if (dialog.type === "custom") {
    return (
      <AlertDialog open={true}>
        <AlertDialogContent>{dialog.children}</AlertDialogContent>
      </AlertDialog>
    );
  }

  const isConfirmDisabled =
    dialog.type === "confirm" && dialog.confirmText
      ? confirmText !== dialog.confirmText
      : false;

  const handleAction = async () => {
    await handleDialogAction(dialog.id, async () =>
      dialog.action.onClick?.(dialog.type === "input" ? inputValue : undefined)
    );
  };

  const handleCancel = async () => {
    if (dialog.cancel?.onClick) {
      await dialog.cancel.onClick();
    } else {
      useDialogStore.getState().removeDialog(dialog.id);
    }
  };

  return (
    <AlertDialog open={true} onOpenChange={handleCancel}>
      <AlertDialogContent>
        <AlertDialogHeader
          className={cn({
            "flex flex-col items-center gap-2": dialog.style === "centered",
          })}
        >
          {dialog.icon && (
            <div className="bg-muted flex h-10 w-10 shrink-0 items-center justify-center rounded-full">
              <dialog.icon className="size-6" />
            </div>
          )}
          <AlertDialogTitle>{dialog.title ?? ""}</AlertDialogTitle>
          {typeof dialog.description === "string" ? (
            <AlertDialogDescription>
              {dialog.description}
            </AlertDialogDescription>
          ) : (
            dialog.description
          )}
        </AlertDialogHeader>

        {dialog.type === "confirm" && dialog.confirmText && (
          <div className="space-y-2">
            <p>
              Type <code className="font-mono">{dialog.confirmText}</code> to
              confirm this action.
            </p>
            <Input
              autoFocus
              value={confirmText}
              onChange={(e) => setConfirmText(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  if (!dialog.loading && !isConfirmDisabled) {
                    void handleAction();
                  }
                }
              }}
            />
          </div>
        )}

        {dialog.type === "input" && (
          <div className="mt-2">
            <Label>{dialog.input.label}</Label>
            <Input
              value={inputValue}
              placeholder={dialog.input.placeholder}
              onChange={(e) => setInputValue(e.target.value)}
              ref={(ref) => ref?.focus()}
              onKeyDown={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  e.preventDefault();
                  if (!dialog.loading && !isConfirmDisabled) {
                    void handleAction();
                  }
                }
              }}
            />
          </div>
        )}

        <AlertDialogFooter>
          <Button
            variant="outline"
            disabled={dialog.loading}
            onClick={handleCancel}
          >
            {dialog.cancel?.label ?? "Cancel"}
          </Button>

          <Button
            disabled={dialog.loading || isConfirmDisabled}
            onClick={handleAction}
            variant={dialog.action.variant ?? "default"}
          >
            {dialog.action.label ?? "OK"}
          </Button>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

"use client";

// Add this file inside app/layout.tsx to render the dialogs.
import { DialogComponent } from "./dialog-component";
import { useDialogStore } from "./dialog-store";

export function DialogManagerRenderer() {
  const activeDialog = useDialogStore((state) => state.activeDialog);

  if (activeDialog) {
    return <DialogComponent dialog={activeDialog} />;
  }

  return null;
}
