"use client";

import { createContext, Fragment, use, useEffect, useRef } from "react";
import type { FieldValues } from "react-hook-form";
import { useHotkeys } from "react-hotkeys-hook";
import { useDebounceFn } from "../../hooks/use-debounce-fn";
import { useWarnIfUnsavedChanges } from "../../hooks/use-warn-if-unsaved-changes";
import type { FormProps } from "../../../components/extended-form";
import { ExtendedForm } from "../../../components/extended-form";

const FormAutoSaveContext = createContext<{
  isDirty: boolean;
  isLoading: boolean;
  cancel: () => void;
  submit: () => void;
} | null>(null);

export const useFormAutoSave = () => {
  const ctx = use(FormAutoSaveContext);

  if (!ctx) {
    throw new Error("FormUnsavedBarContext is not provided");
  }

  return ctx;
};

export const FormAutoSave = <T extends FieldValues>({
  children,
  autoSaveMs,
  action,
  ...props
}: FormProps<T> & { autoSaveMs?: number; action?: string }) => {
  const buttonRef = useRef<HTMLButtonElement>(null);

  const submit = () => buttonRef.current?.click();
  const cancel = () => props.form.reset();

  const isDirty = props.form.formState.isDirty;

  useHotkeys("mod+s", submit, {
    enabled: isDirty,
    enableOnContentEditable: true,
    enableOnFormTags: true,
    preventDefault: true,
  });

  useWarnIfUnsavedChanges(
    isDirty,
    "You have unsaved changes. Please save or cancel your changes before leaving."
  );

  return (
    <FormAutoSaveContext.Provider
      value={{
        isDirty,
        isLoading: props.form.formState.isSubmitting,
        cancel,
        submit,
      }}
    >
      <Fragment>
        <ExtendedForm {...props} disabled={false}>
          {children}
          <button type="submit" className="hidden" ref={buttonRef} />
        </ExtendedForm>
      </Fragment>
    </FormAutoSaveContext.Provider>
  );
};

export const FormAutoSaveWatch = <T extends FieldValues>(
  props: Pick<FormProps<T>, "form"> & { autoSaveMs?: number }
) => {
  const lastFormStateRef = useRef<string | null>(null);
  const watchedField = props.form.watch();
  const ctx = useFormAutoSave();

  const debounce = useDebounceFn(() => {
    const json = JSON.stringify(watchedField);
    if (json === lastFormStateRef.current) return;
    lastFormStateRef.current = json;

    ctx.submit();
  }, props.autoSaveMs);

  useEffect(() => {
    debounce();
  }, [debounce, watchedField]);

  return null;
};

import { Button } from "@/components/ui/button";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { TriangleAlert } from "lucide-react";
import { AnimatePresence, motion } from "motion/react";
import type { ReactNode } from "react";
import { createPortal } from "react-dom";
import { useIsClient } from "../../hooks/use-is-client";
import { useFormAutoSave } from "./form-management";
import { LoadingButton } from "../../../components/submit-button";

type FormAutoSaveStickyBarProps = {
  actionLabel?: string;
  cancelLabel?: string;
};

// eslint-disable-next-line @typescript-eslint/promise-function-async
export const FormAutoSaveStickyBar = (props: FormAutoSaveStickyBarProps) => {
  const ctx = useFormAutoSave();
  const onSubmit = ctx.submit;
  const onCancel = ctx.cancel;
  const isLoading = ctx.isLoading;
  const isDirty = ctx.isDirty;
  const isClient = useIsClient();

  const isShow = isDirty;

  if (!isClient) {
    return null;
  }

  return createPortal(
    <div
      className="pointer-events-none fixed inset-x-0 bottom-0 flex items-center justify-center overflow-hidden py-4"
      style={{
        zIndex: 999,
      }}
    >
      <AnimatePresence>
        {isShow ? (
          <motion.div
            key="save-bar"
            initial={{
              opacity: 0,
              y: 20,
            }}
            animate={{
              opacity: 1,
              y: 0,
            }}
            exit={{
              opacity: [1, 1, 0],
              y: [0, -10, 20],
              transition: {
                duration: 0.5,
              },
            }}
            className="bg-card shadow-background pointer-events-auto flex w-full max-w-sm items-center gap-2 rounded-full border py-1 pr-2 pl-4 shadow-2xl lg:py-2"
          >
            <TriangleAlert className="text-muted-foreground size-4" />
            <p className="text-muted-foreground text-sm">Unsaved Changes</p>
            <div className="flex-1" />
            <Button
              variant="outline"
              size="sm"
              onClick={onCancel}
              className="rounded-full"
            >
              {props.cancelLabel ?? "Reset"}
            </Button>
            <Tooltip>
              <TooltipTrigger asChild>
                <LoadingButton
                  size="sm"
                  loading={isLoading}
                  variant="default"
                  onClick={onSubmit}
                  className="rounded-full"
                >
                  {props.actionLabel ?? "Save"}
                </LoadingButton>
              </TooltipTrigger>
              <TooltipContent>CMD+S</TooltipContent>
            </Tooltip>
          </motion.div>
        ) : null}
      </AnimatePresence>
    </div>,
    document.body
  ) as ReactNode;
};
